#!/bin/bash

bytecode=''

isDivisible="False"
while [ "$isDivisible" != "True" ]; do
    returnText=`cast create2 -i "$bytecode" -d 0x652c9ACcC53e765e1d96e2455E618dAaB79bA595 -s 0`

    isDivisible=$(python3 divide.py "$returnText")
    
    if [ "$isDivisible" == "True" ]; then
        echo "$returnText"
    else
        echo " "
    fi
done